var couch = require('./couch/couch.js');
couch.groups = require('./couch/groups.js');

var guard = require('circle-blvd/errors').guard;

module.exports = function () {

	var addGroup = function(group, success, failure) {
		var newGroup = {
			name: group.name,
			projectId: group.projectId,
			isPermanent: group.isPermanent
		};
		
		couch.groups.add(newGroup, guard(failure, success));
	};

	var removeGroup = function(group, success, failure) {
		couch.groups.remove(group, guard(failure, function (body) {
			return success();
		}));
	};

	var findGroupById = function (groupId, callback) {
		// TODO: Make the other get-by-id functions like this, perhaps.
		couch.docs.get(groupId, guard(callback, function (body) {
			if (body.type === "group") {
				return callback(null, body);
			}
			else {
				return callback({
					message: "Document is not a group: " + groupId
				});
			}
		}));
	};

	var findGroupsByProjectId = function (projectId, callback) {

		var prepareGroups = function (dbGroups) {
			var groups = [];
			dbGroups.forEach(function (group, index, array) {
				// TODO: Not sure we need this modelGroup
				// business, except to cause more work
				// in the future.
				var modelGroup = {
					id: group._id,
					projectId: group.projectId,
					name: group.name,
					isPermanent: group.isPermanent
				};

				groups.push(modelGroup);
			});

			callback(null, groups);
		};

		couch.groups.findByProjectId(projectId, 
			guard(callback, prepareGroups));
	};

	var findImpliedByCircleId = function (circleId, callback) {
		findGroupsByProjectId(circleId, guard(callback, function (groups) {
			var impliedGroup = undefined;
			groups.forEach(function (group) {
				if (group.name === "_implied") {
					impliedGroup = group;
				}
			});

			callback(null, impliedGroup);
		}));
	};

	var findGroupsByUser = function(user, callback) {
		couch.groups.findByUser(user, callback);
	};

	return {
		add: addGroup,
		remove: removeGroup,
		findById: findGroupById,
		findByProjectId: findGroupsByProjectId,
		findImpliedByCircleId: findImpliedByCircleId,
		findByUser: findGroupsByUser
	};
}();